function memorizer(fun) {
  let cache = {}
  return function (n) {
    if (cache[n] !== undefined) {
      return cache[n]
    }
    let result = fun(n)
    cache[n] = result
    return result
  }
}
const addOne = memorizer((num: number) => num + 1)

