function useLocalState(key, defaultValue) {
  const [state, setState] = useState(() => {
    return localStorage.getItem(key) || defaultValue
  })
  useEffect(() => {
    localStorage.setItem(key, state)
  }, [key, state])
  return [state, setState]
}

